import React from 'react';
import { Link } from 'react-router-dom';
import './Pages.css';

const TrackOrders = () => {
  const activeOrders = [
    {
      id: 'WK003',
      dish: 'Margherita Pizza',
      status: 'In Transit',
      estimatedDelivery: '30 minutes',
      driver: 'John D.',
      location: 'On the way to your location'
    }
  ];

  return (
    <div className="page-container">
      <div className="page-header">
        <Link to="/" className="back-button">
          ‚Üê Back to Home
        </Link>
        <h1>Track Orders</h1>
      </div>

      <div className="page-content">
        {activeOrders.length > 0 ? (
          <div className="orders-list">
            {activeOrders.map((order) => (
              <div key={order.id} className="order-card">
                <div className="order-header">
                  <h3>{order.dish}</h3>
                  <span className={`status-badge ${order.status.toLowerCase().replace(' ', '-')}`}>
                    {order.status}
                  </span>
                </div>

                <div className="order-details">
                  <p><strong>Order ID:</strong> {order.id}</p>
                  <p><strong>Estimated Delivery:</strong> {order.estimatedDelivery}</p>
                  <p><strong>Driver:</strong> {order.driver}</p>
                  <p><strong>Location:</strong> {order.location}</p>
                </div>

                <div className="order-actions">
                  <button className="action-button">Track on Map</button>
                  <button className="action-button secondary">Contact Driver</button>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="empty-state">
            <div className="empty-icon">üöö</div>
            <h3>No Active Orders</h3>
            <p>Your current deliveries will appear here</p>
            <Link to="/" className="cta-button">
              Order Something
            </Link>
          </div>
        )}
      </div>
    </div>
  );
};

export default TrackOrders;
