import React, { useState, useEffect, useCallback, useRef } from 'react';
import axios from 'axios';
import { CopyToClipboard } from 'react-copy-to-clipboard';
import './App.css';

// Import components
import { LoginModal, SignupModal, LandingPage, MainApp } from './components';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [showLoginForm, setShowLoginForm] = useState(false);
  const [showSigninForm, setShowSigninForm] = useState(false);
  const [dishName, setDishName] = useState('');
  const [servings, setServings] = useState(2);
  const [ingredients, setIngredients] = useState([]);
  const [recipeInfo, setRecipeInfo] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [copied, setCopied] = useState(false);
  const [recentSearches, setRecentSearches] = useState([]);

  // Load recent searches from localStorage on component mount
  useEffect(() => {
    const saved = localStorage.getItem('weKnowRecentSearches');
    if (saved) {
      setRecentSearches(JSON.parse(saved));
    }
  }, []);

  // Save recent searches to localStorage whenever it changes
  useEffect(() => {
    localStorage.setItem('weKnowRecentSearches', JSON.stringify(recentSearches));
  }, [recentSearches]);

  // Check if user is already authenticated
  useEffect(() => {
    const checkAuth = async () => {
      const token = localStorage.getItem('weKnowAuthToken');
      if (token) {
        try {
          // Verify token with backend
          const response = await axios.get('/auth/me', {
            headers: {
              'Authorization': `Bearer ${token}`
            }
          });

          if (response.data.success) {
            setIsAuthenticated(true);
          } else {
            // Token is invalid, clear storage
            localStorage.removeItem('weKnowAuthToken');
            localStorage.removeItem('weKnowUser');
            setIsAuthenticated(false);
          }
        } catch (err) {
          console.error('Auth check error:', err);
          // Token is invalid, clear storage
          localStorage.removeItem('weKnowAuthToken');
          localStorage.removeItem('weKnowUser');
          setIsAuthenticated(false);
        }
      }
    };

    checkAuth();
  }, []);

  const handleLogout = async () => {
    try {
      const token = localStorage.getItem('weKnowAuthToken');
      if (token) {
        // Call logout endpoint
        await axios.post('/auth/logout', {}, {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });
      }
    } catch (err) {
      console.error('Logout error:', err);
      // Continue with logout even if API call fails
    } finally {
      // Clear local storage
      localStorage.removeItem('weKnowAuthToken');
      localStorage.removeItem('weKnowUser');
      setIsAuthenticated(false);
      setShowLoginForm(false);
      setShowSigninForm(false);
      setIngredients([]);
      setRecipeInfo(null);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!dishName.trim()) {
      setError('Please enter a dish name');
      return;
    }

    setLoading(true);
    setError('');
    setIngredients([]);
    setRecipeInfo(null);

    try {
      console.log('Sending request for:', dishName.trim(), 'servings:', servings);
      const response = await axios.post('/ingredients', {
        dish_name: dishName.trim(),
        servings: servings
      });

      console.log('Response received:', response.data);

      if (response.data.success) {
        console.log('Setting ingredients:', response.data.ingredients);
        console.log('Setting recipe info:', response.data.recipe_info);
        setIngredients(response.data.ingredients);
        setRecipeInfo(response.data.recipe_info);

        // Add to recent searches
        const newSearch = {
          dish: dishName.trim(),
          timestamp: new Date().toISOString()
        };

        setRecentSearches(prev => {
          const filtered = prev.filter(item => item.dish !== dishName.trim());
          return [newSearch, ...filtered].slice(0, 5); // Keep only 5 recent searches
        });
      } else {
        setError(response.data.error || 'Failed to fetch ingredients');
      }
    } catch (err) {
      console.error('Error fetching ingredients:', err);
      if (err.response?.data?.error) {
        setError(err.response.data.error);
      } else if (err.response?.status === 404) {
        setError(`No recipe found for "${dishName}". Try a different dish name.`);
      } else {
        setError('Failed to connect to the server. Please try again.');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleCopy = () => {
    setCopied(true);
    setTimeout(() => setCopied(false), 2000);
  };

  const handleDownload = () => {
    if (!ingredients.length) return;

    const content = generateGroceryList();
    const blob = new Blob([content], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `${dishName.replace(/[^a-z0-9]/gi, '_').toLowerCase()}_grocery_list.txt`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  const generateGroceryList = () => {
    let content = `Grocery List for ${recipeInfo?.title || dishName}\n`;
    content += `Servings: ${servings}\n`;
    content += `Generated by We Know\n\n`;
    content += `Ingredients:\n`;
    content += `============\n\n`;

    ingredients.forEach((ingredient, index) => {
      const quantity = ingredient.quantity;
      const unit = ingredient.unit || '';
      const name = ingredient.ingredient || 'Unknown ingredient';

      if (quantity !== null) {
        content += `${index + 1}. ${quantity} ${unit} ${name}\n`;
      } else {
        content += `${index + 1}. ${name}\n`;
      }
    });

    return content;
  };

  const handleRecentSearchClick = (dish) => {
    setDishName(dish);
  };

  const handleDishNameChange = useCallback((e) => {
    setDishName(e.target.value);
  }, []);

  const handleServingsChange = useCallback((e) => {
    setServings(parseInt(e.target.value));
  }, []);

  const dishInputRef = useRef(null);

  useEffect(() => {
    if (dishInputRef.current) {
      dishInputRef.current.focus();
    }
  }, [dishName]);

  return (
    <div className="App">
      {isAuthenticated ? (
        <MainApp 
          dish={dishName}
          setDish={setDishName}
          ingredients={ingredients}
          loading={loading}
          error={error}
          handleSubmit={handleSubmit}
          handleCopy={handleCopy}
          handleDownload={handleDownload}
          generateGroceryList={generateGroceryList}
          handleRecentSearchClick={handleRecentSearchClick}
          recentSearches={recentSearches}
          user={JSON.parse(localStorage.getItem('weKnowUser') || '{}')}
          handleLogout={handleLogout}
          recipeInfo={recipeInfo}
          servings={servings}
          setServings={setServings}
        />
      ) : (
        <LandingPage 
          onLoginClick={() => setShowLoginForm(true)}
          onSignupClick={() => setShowSigninForm(true)}
        />
      )}

      {/* Login Form Modal */}
      {showLoginForm && (
        <LoginModal
          isOpen={showLoginForm}
          onClose={() => setShowLoginForm(false)}
          onLogin={() => setIsAuthenticated(true)}
        />
      )}

      {/* Signin Form Modal */}
      {showSigninForm && (
        <SignupModal
          isOpen={showSigninForm}
          onClose={() => setShowSigninForm(false)}
          onSignup={() => setIsAuthenticated(true)}
        />
      )}
    </div>
  );
}

export default App;
