import React, { useState, useEffect, useRef } from 'react';
import './MapAddressSelector.css';

const MapAddressSelector = ({ onAddressSelect, onClose }) => {
  const [map, setMap] = useState(null);
  const [marker, setMarker] = useState(null);
  const [selectedAddress, setSelectedAddress] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const mapRef = useRef(null);

  useEffect(() => {
    // Load Google Maps API
    const loadGoogleMaps = () => {
      if (window.google && window.google.maps) {
        initializeMap();
        return;
      }

      const script = document.createElement('script');
      script.src = `https://maps.googleapis.com/maps/api/js?key=AIzaSyCHATAMkCfr-KRmodm2qAQE97THhtbiovk&libraries=places`;
      script.async = true;
      script.defer = true;
      script.onload = initializeMap;
      script.onerror = () => {
        console.error('Failed to load Google Maps API');
        setIsLoading(false);
      };
      document.head.appendChild(script);
    };

    loadGoogleMaps();
  }, []);

  const initializeMap = () => {
    if (!mapRef.current) return;

    const defaultLocation = { lat: 40.7128, lng: -74.0060 }; // New York City

    const mapInstance = new window.google.maps.Map(mapRef.current, {
      center: defaultLocation,
      zoom: 13,
      mapTypeControl: false,
      streetViewControl: false,
      fullscreenControl: false,
      styles: [
        {
          featureType: 'poi',
          elementType: 'labels',
          stylers: [{ visibility: 'off' }]
        }
      ]
    });

    const markerInstance = new window.google.maps.Marker({
      position: defaultLocation,
      map: mapInstance,
      draggable: true,
      title: 'Drag to select address'
    });

    // Add click listener to map
    mapInstance.addListener('click', (event) => {
      const position = event.latLng;
      markerInstance.setPosition(position);
      reverseGeocode(position);
    });

    // Add drag listener to marker
    markerInstance.addListener('dragend', (event) => {
      const position = event.latLng;
      reverseGeocode(position);
    });

    setMap(mapInstance);
    setMarker(markerInstance);
    setIsLoading(false);
  };

  const reverseGeocode = (position) => {
    const geocoder = new window.google.maps.Geocoder();

    geocoder.geocode({ location: position }, (results, status) => {
      if (status === 'OK' && results[0]) {
        const addressComponents = results[0].address_components;
        const formattedAddress = results[0].formatted_address;

        // Parse address components
        let streetNumber = '';
        let route = '';
        let city = '';
        let state = '';
        let zipCode = '';

        addressComponents.forEach(component => {
          const types = component.types;

          if (types.includes('street_number')) {
            streetNumber = component.long_name;
          } else if (types.includes('route')) {
            route = component.long_name;
          } else if (types.includes('locality')) {
            city = component.long_name;
          } else if (types.includes('administrative_area_level_1')) {
            state = component.short_name;
          } else if (types.includes('postal_code')) {
            zipCode = component.long_name;
          }
        });

        const address = {
          fullAddress: formattedAddress,
          streetAddress: `${streetNumber} ${route}`.trim(),
          city: city,
          state: state,
          zip: zipCode,
          lat: position.lat(),
          lng: position.lng()
        };

        setSelectedAddress(address);
      }
    });
  };

  const handleConfirmAddress = () => {
    if (selectedAddress) {
      onAddressSelect(selectedAddress);
    }
  };

  const handleSearchLocation = (searchQuery) => {
    if (!map || !searchQuery.trim()) return;

    const geocoder = new window.google.maps.Geocoder();

    geocoder.geocode({ address: searchQuery }, (results, status) => {
      if (status === 'OK' && results[0]) {
        const position = results[0].geometry.location;
        map.setCenter(position);
        map.setZoom(16);
        marker.setPosition(position);
        reverseGeocode(position);
      }
    });
  };

  return (
    <div className="map-address-selector">
      <div className="map-header">
        <h3>Select Address from Map</h3>
        <button className="close-button" onClick={onClose}>✕</button>
      </div>

      <div className="search-container">
        <input
          type="text"
          placeholder="Search for an address..."
          className="address-search"
          onKeyPress={(e) => {
            if (e.key === 'Enter') {
              handleSearchLocation(e.target.value);
            }
          }}
        />
        <button 
          className="search-button"
          onClick={() => {
            const input = document.querySelector('.address-search');
            handleSearchLocation(input.value);
          }}
        >
          🔍
        </button>
      </div>

      <div className="map-container">
        {isLoading && (
          <div className="map-loading">
            <div className="loading-spinner"></div>
            <p>Loading map...</p>
          </div>
        )}
        <div ref={mapRef} className="google-map">
          <div className="mock-map">
            <div className="mock-map-content">
              <div className="mock-map-placeholder">
                <div className="map-icon">🗺️</div>
                <h4>Interactive Map</h4>
                <p>Click anywhere on the map to select an address</p>
                <p className="demo-note">(Demo Mode - Mock Implementation)</p>
                <div className="api-note">
                  <p><strong>To use real Google Maps:</strong></p>
                  <ol>
                    <li>Get a Google Maps API key from Google Cloud Console</li>
                    <li>Enable Maps JavaScript API, Geocoding API, and Places API</li>
                    <li>Replace the mock implementation with real API calls</li>
                  </ol>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {selectedAddress && (
        <div className="selected-address">
          <h4>Selected Address:</h4>
          <p>{selectedAddress.fullAddress}</p>
          <div className="address-details">
            <span><strong>Street:</strong> {selectedAddress.streetAddress}</span>
            <span><strong>City:</strong> {selectedAddress.city}</span>
            <span><strong>State:</strong> {selectedAddress.state}</span>
            <span><strong>ZIP:</strong> {selectedAddress.zip}</span>
          </div>
        </div>
      )}

      <div className="map-actions">
        <button 
          className="cancel-button" 
          onClick={onClose}
        >
          Cancel
        </button>
        <button 
          className="confirm-button" 
          onClick={handleConfirmAddress}
          disabled={!selectedAddress}
        >
          Confirm Address
        </button>
      </div>
    </div>
  );
};

export default MapAddressSelector;
