import React, { useState, useEffect, useRef } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import axios from 'axios';
import './App.css';
import userDataService from './services/userDataService';
import { clearAllLocalStorage, logLocalStorageData } from './utils/clearLocalStorage';

// Import components
import { LoginModal, SignupModal, LandingPage, SignupScreen } from './components';
import AppLayout from './components/AppLayout.jsx';

// Import pages
import HomePage from './pages/HomePage';
import OrderHistory from './pages/OrderHistory';
import SavedAddresses from './pages/SavedAddresses';
import Settings from './pages/Settings';
import TrackOrders from './pages/TrackOrders';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [showLoginForm, setShowLoginForm] = useState(false);
  const [showSignupScreen, setShowSignupScreen] = useState(false);
  const [isLoadingUserData, setIsLoadingUserData] = useState(false);
  const [dishName, setDishName] = useState('');
  const [servings, setServings] = useState(2);
  const [ingredients, setIngredients] = useState([]);
  const [recipeInfo, setRecipeInfo] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const [recentSearches, setRecentSearches] = useState([]);
  const [orders, setOrders] = useState([]);

  // Debug logging for authentication state
  console.log('App Debug - isAuthenticated:', isAuthenticated);
  console.log('App Debug - showLoginForm:', showLoginForm);
  console.log('App Debug - showSignupScreen:', showSignupScreen);

  // Function to navigate to landing page (home)
  const navigateToHome = () => {
    console.log('navigateToHome called - setting isAuthenticated to false');
    setIsAuthenticated(false);
    setShowLoginForm(false);
    setShowSignupScreen(false);
    // Clear any authentication data
    localStorage.removeItem('weKnowToken');
    localStorage.removeItem('weKnowUser');
    localStorage.removeItem('weKnowOrders');
    localStorage.removeItem('weKnowRecentSearches');
    // Scroll to top of the page
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  // Load recent searches from database on component mount
  useEffect(() => {
    const loadRecentSearches = async () => {
      if (isAuthenticated) {
        try {
          const searches = await userDataService.getRecentSearches();
          setRecentSearches(searches.map(search => search.dish_name));
        } catch (error) {
          console.error('Error loading recent searches:', error);
        }
      } else {
        // Clear local state when not authenticated
        setRecentSearches([]);
      }
    };

    loadRecentSearches();
  }, [isAuthenticated]);

  // Load orders from database when authenticated
  useEffect(() => {
    const loadOrders = async () => {
      if (isAuthenticated) {
        try {
          const userOrders = await userDataService.getOrders();
          setOrders(userOrders);
          // Save to localStorage for other components to access
          localStorage.setItem('weKnowOrders', JSON.stringify(userOrders));
        } catch (error) {
          console.error('Error loading orders:', error);
        }
      } else {
        // Clear local state when not authenticated
        setOrders([]);
        localStorage.removeItem('weKnowOrders');
      }
    };

    loadOrders();
  }, [isAuthenticated]);

  // Check if user is already authenticated
  useEffect(() => {
    const checkAuth = async () => {
      const token = localStorage.getItem('weKnowToken');
      if (token) {
        try {
          // Verify token with backend
          const response = await axios.get('http://localhost:5001/auth/me', {
            headers: {
              'Authorization': `Bearer ${token}`
            }
          });

          if (response.data.success) {
            setIsAuthenticated(true);
            // Clear any old localStorage data that might interfere
            localStorage.removeItem('weKnowRecentSearches');
            localStorage.removeItem('weKnowAuthToken');
          } else {
            // Token is invalid, clear storage
            userDataService.clearUserData();
            setIsAuthenticated(false);
          }
        } catch (err) {
          console.error('Auth check error:', err);
          // Token is invalid, clear storage
          userDataService.clearUserData();
          setIsAuthenticated(false);
        }
      }
    };

    checkAuth();
  }, []);

  const handleLogout = async () => {
    try {
      const token = localStorage.getItem('weKnowToken');
      if (token) {
        // Call logout endpoint
        await axios.post('http://localhost:5001/auth/logout', {}, {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });
      }
    } catch (err) {
      console.error('Logout error:', err);
      // Continue with logout even if API call fails
    } finally {
      // Clear authentication but keep data in database
      userDataService.clearUserData();
      setIsAuthenticated(false);
      setShowLoginForm(false);
      setShowSignupScreen(false);
      setIngredients([]);
      setRecipeInfo(null);
      // Recent searches and orders will be cleared by useEffect when isAuthenticated becomes false
    }
  };

  const handleSubmit = async (e, overrideDishName = null) => {
    e.preventDefault();

    const dishToUse = overrideDishName || dishName;

    if (!dishToUse.trim()) {
      setError('Please enter a dish name');
      return;
    }

    setLoading(true);
    setError('');
    setIngredients([]);
    setRecipeInfo(null);

    try {
      const response = await axios.post('http://localhost:5001/ingredients', {
        dish_name: dishToUse.trim(),
        servings: servings
      });

      console.log('Response received:', response.data);

      if (response.data.success) {
        console.log('Setting ingredients:', response.data.ingredients);
        console.log('Setting recipe info:', response.data.recipe_info);
        setIngredients(response.data.ingredients);
        setRecipeInfo(response.data.recipe_info);

        // Add to recent searches in database
        if (isAuthenticated) {
          try {
            await userDataService.addRecentSearch(dishToUse.trim());
            // Update local state
            setRecentSearches(prev => {
              const filtered = prev.filter(item => item !== dishToUse.trim());
              return [dishToUse.trim(), ...filtered].slice(0, 5); // Keep only 5 recent searches
            });
          } catch (error) {
            console.error('Error saving recent search:', error);
          }
        } else {
          // For non-authenticated users, just update local state
          setRecentSearches(prev => {
            const filtered = prev.filter(item => item !== dishToUse.trim());
            return [dishToUse.trim(), ...filtered].slice(0, 5);
          });
        }
      } else {
        setError(response.data.error || 'Failed to fetch ingredients');
      }
    } catch (err) {
      console.error('Error fetching ingredients:', err);
      if (err.response?.data?.error) {
        setError(err.response.data.error);
      } else if (err.response?.status === 404) {
        setError(`No recipe found for "${dishToUse}". Try a different dish name.`);
      } else {
        setError('Failed to connect to the server. Please try again.');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleCopy = () => {
    // Copy functionality handled in MainApp component
  };

  const handleDownload = () => {
    if (!ingredients.length) return;

    const content = generateGroceryList();
    const blob = new Blob([content], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `${dishName.replace(/[^a-z0-9]/gi, '_').toLowerCase()}_grocery_list.txt`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  const generateGroceryList = () => {
    let content = `Grocery List for ${recipeInfo?.title || dishName}\n`;
    content += `Servings: ${servings}\n`;
    content += `Generated by We Know\n\n`;
    content += `Ingredients:\n`;
    content += `============\n\n`;

    ingredients.forEach((ingredient, index) => {
      const quantity = ingredient.quantity;
      const unit = ingredient.unit || '';
      const name = ingredient.ingredient || 'Unknown ingredient';

      if (quantity !== null) {
        content += `${index + 1}. ${quantity} ${unit} ${name}\n`;
      } else {
        content += `${index + 1}. ${name}\n`;
      }
    });

    return content;
  };

  const handleRecentSearchClick = async (dish) => {
    setDishName(dish);

    // Add to recent searches in database
    if (isAuthenticated) {
      try {
        await userDataService.addRecentSearch(dish);
        // Update local state to reflect the new search
        setRecentSearches(prev => {
          const filtered = prev.filter(item => item !== dish);
          return [dish, ...filtered].slice(0, 5);
        });
      } catch (error) {
        console.error('Error adding recent search:', error);
      }
    }
  };

  const addOrder = async (order) => {
    // Save to database first
    if (isAuthenticated) {
      try {
        const savedOrder = await userDataService.addOrder({
          dish_name: order.dish_name || order.dishName,
          ingredients: order.ingredients,
          servings: order.servings || 2
        });

        // Add to local state with database ID
        const newOrder = {
          ...order,
          id: savedOrder.order_id,
          timestamp: new Date().toISOString()
        };
        setOrders(prevOrders => [...prevOrders, newOrder]);
        // Save to localStorage
        localStorage.setItem('weKnowOrders', JSON.stringify([...orders, newOrder]));
      } catch (error) {
        console.error('Error saving order to database:', error);
        // Still add to local state even if database save fails
        setOrders(prevOrders => [...prevOrders, order]);
      }
    } else {
      // For non-authenticated users, just add to local state
      setOrders(prevOrders => [...prevOrders, order]);
    }
  };



  const dishInputRef = useRef(null);

  useEffect(() => {
    if (dishInputRef.current) {
      dishInputRef.current.focus();
    }
  }, [dishName]);

  return (
    <Router>
      <div className="App">
        {/* Temporary debug button - remove this after testing */}
        {!isAuthenticated && (
          <div style={{ 
            position: 'fixed', 
            top: '10px', 
            right: '10px', 
            zIndex: 9999,
            background: '#ff4444',
            color: 'white',
            padding: '10px',
            borderRadius: '5px',
            fontSize: '12px'
          }}>
            <button onClick={() => {
              clearAllLocalStorage();
              logLocalStorageData();
              alert('LocalStorage cleared! Please refresh the page.');
            }}>
              🧹 Clear All Data
            </button>
          </div>
        )}
        {/* Clear orders button for testing */}
        {isAuthenticated && (
          <div style={{ 
            position: 'fixed', 
            top: '10px', 
            right: '10px', 
            zIndex: 9999,
            background: '#ff8800',
            color: 'white',
            padding: '10px',
            borderRadius: '5px',
            fontSize: '12px'
          }}>
            <button onClick={async () => {
              try {
                await userDataService.clearOrders();
                setOrders([]);
                localStorage.removeItem('weKnowOrders');
                alert('Orders cleared from database! Check backend logs.');
              } catch (error) {
                alert('Error clearing orders: ' + error.message);
              }
            }}>
              🗑️ Clear Orders
            </button>
          </div>
        )}
        {isAuthenticated ? (
          <AppLayout 
            user={JSON.parse(localStorage.getItem('weKnowUser') || '{}')}
            handleLogout={handleLogout}
            navigateToHome={navigateToHome}
          >
            <Routes>
              <Route path="/" element={
                <HomePage 
                  dish={dishName}
                  setDish={setDishName}
                  ingredients={ingredients}
                  loading={loading}
                  setLoading={setLoading}
                  error={error}
                  handleSubmit={handleSubmit}
                  handleCopy={handleCopy}
                  handleDownload={handleDownload}
                  generateGroceryList={generateGroceryList}
                  handleRecentSearchClick={handleRecentSearchClick}
                  recentSearches={recentSearches}
                  recipeInfo={recipeInfo}
                  servings={servings}
                  setServings={setServings}
                  orders={orders}
                  addOrder={addOrder}
                />
              } />
              <Route path="/order-history" element={<OrderHistory />} />
              <Route path="/saved-addresses" element={<SavedAddresses />} />
              <Route path="/track-orders" element={<TrackOrders />} />
              <Route path="/settings" element={<Settings />} />
              <Route path="*" element={<Navigate to="/" replace />} />
            </Routes>
          </AppLayout>
        ) : showSignupScreen ? (
          <SignupScreen
            onSwitchToLogin={() => {
              setShowSignupScreen(false);
              setShowLoginForm(true);
            }}
            onSignupSuccess={(userData) => {
              setIsAuthenticated(true);
              setShowSignupScreen(false);
              // Data will be loaded from database via useEffect
            }}
          />
        ) : (
          <LandingPage 
            onLoginClick={() => setShowLoginForm(true)}
            onSignupClick={() => setShowSignupScreen(true)}
            navigateToHome={navigateToHome}
          />
        )}

        {/* Login Form Modal */}
        {showLoginForm && (
          <LoginModal
            isOpen={showLoginForm}
            onClose={() => setShowLoginForm(false)}
            onLogin={() => {
              console.log('onLogin callback called - setting isAuthenticated to true');
              setIsAuthenticated(true);
              // Data will be loaded from database via useEffect
            }}
          />
        )}


      </div>
    </Router>
  );
}

export default App;
