import React, { useState } from 'react';
import GroceryCartService from './GroceryCartService.js';

const GroceryOrderButton = ({ ingredients, dishName }) => {
  // Grocery app configurations with cart APIs and deep links
  const groceryApps = {
    blinkit: {
      name: 'Blinkit',
      baseUrl: 'https://www.blinkit.com/search?q=',
      cartUrl: 'https://blinkit.com/cart/add',
      deepLink: 'blinkit://cart/add',
      icon: 'üõí',
      color: '#ff6b35',
      hasCartAPI: true
    },
    zepto: {
      name: 'Zepto',
      baseUrl: 'https://www.zepto.in/search?q=',
      cartUrl: 'https://www.zepto.in/cart/add',
      deepLink: 'zepto://cart/add',
      icon: '‚ö°',
      color: '#00b894',
      hasCartAPI: true
    },
    bigbasket: {
      name: 'BigBasket',
      baseUrl: 'https://www.bigbasket.com/ps/?q=',
      cartUrl: 'https://www.bigbasket.com/cart/add',
      deepLink: 'bigbasket://cart/add',
      icon: 'üõçÔ∏è',
      color: '#0984e3',
      hasCartAPI: true
    }
  };

  // Function to create search query from ingredients array
  const createSearchQuery = (ingredients) => {
    if (!ingredients || !Array.isArray(ingredients) || ingredients.length === 0) {
      return null;
    }

    // Extract ingredient names and clean them
    const ingredientNames = ingredients.map(ingredient => {
      // Handle both string and object formats
      const ingredientName = typeof ingredient === 'string' 
        ? ingredient 
        : ingredient.ingredient || ingredient.name || '';

      // Clean the ingredient name (remove quantities, units, etc.)
      return ingredientName
        .replace(/[\d.,\/\s]+(?:grams?|g|kilos?|kg|ounces?|oz|pounds?|lbs?|cups?|tablespoons?|tbsp|teaspoons?|tsp|ml|l|pieces?|pcs?)/gi, '')
        .replace(/[^\w\s]/g, '')
        .trim();
    })
    .filter(name => name.length > 0); // Remove empty strings

    return ingredientNames.join('+');
  };

  // Function to add ingredients to grocery app cart
  const addToGroceryCart = async (appKey) => {
    const app = groceryApps[appKey];
    const searchQuery = createSearchQuery(ingredients);

    if (!searchQuery) {
      alert('No ingredients available for ordering. Please try again.');
      return;
    }

    try {
      // Try to use cart API first (if available)
      if (app.hasCartAPI) {
        await addToCartViaAPI(app, ingredients);
      } else {
        // Fallback to search page
        const url = `${app.baseUrl}${encodeURIComponent(searchQuery)}`;
        window.open(url, '_blank', 'noopener,noreferrer');
      }
    } catch (error) {
      console.error(`Error adding to ${app.name} cart:`, error);
      // Fallback to search page
      const url = `${app.baseUrl}${encodeURIComponent(searchQuery)}`;
      window.open(url, '_blank', 'noopener,noreferrer');
    }
  };

  // Function to add items to cart via API
  const addToCartViaAPI = async (app, ingredients) => {
    const cartItems = ingredients.map(ingredient => {
      const ingredientName = typeof ingredient === 'string' 
        ? ingredient 
        : ingredient.ingredient || ingredient.name || '';

      return {
        name: ingredientName,
        quantity: ingredient.quantity || 1,
        unit: ingredient.unit || 'piece'
      };
    });

    // Try deep link first (for mobile apps)
    if (navigator.userAgent.includes('Mobile')) {
      try {
        window.location.href = app.deepLink;
        return;
      } catch (error) {
        console.log('Deep link failed, trying web API');
      }
    }

    // Try web cart API
    const response = await fetch(app.cartUrl, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        items: cartItems,
        source: 'weknow-app'
      })
    });

    if (response.ok) {
      // Successfully added to cart
      alert(`‚úÖ Ingredients added to ${app.name} cart successfully!`);
      // Open the cart page
      window.open(`${app.cartUrl.replace('/add', '')}`, '_blank', 'noopener,noreferrer');
    } else {
      throw new Error(`Failed to add to ${app.name} cart`);
    }
  };

  // Check if ingredients are available
  const hasIngredients = ingredients && Array.isArray(ingredients) && ingredients.length > 0;
  const searchQuery = createSearchQuery(ingredients);

  if (!hasIngredients) {
    return (
      <div className="grocery-order-section">
        <div className="no-ingredients-message">
          <p>‚ö†Ô∏è No ingredients available for this dish.</p>
          <p>Please try selecting a different dish or contact support.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="grocery-order-section">
      <div className="grocery-order-header">
        <h3>üõí Order Ingredients</h3>
        <p>Get fresh ingredients delivered to your doorstep</p>
      </div>

      <div className="ingredients-preview">
        <h4>Ingredients to order:</h4>
        <div className="ingredients-list">
          {ingredients.map((ingredient, index) => {
            const ingredientName = typeof ingredient === 'string' 
              ? ingredient 
              : ingredient.ingredient || ingredient.name || 'Unknown ingredient';
            return (
              <span key={index} className="ingredient-tag">
                {ingredientName}
              </span>
            );
          })}
        </div>
      </div>

      <div className="grocery-apps-grid">
        {Object.entries(groceryApps).map(([appKey, app]) => (
          <button
            key={appKey}
            className="grocery-app-button"
            onClick={() => addToGroceryCart(appKey)}
            style={{ '--app-color': app.color }}
          >
            <span className="app-icon">{app.icon}</span>
            <span className="app-name">Add to {app.name} Cart</span>
            <span className="app-subtitle">Direct cart addition</span>
          </button>
        ))}
      </div>

      <div className="search-preview">
        <p className="search-preview-text">
          <strong>Search query:</strong> {searchQuery}
        </p>
      </div>
    </div>
  );
};

export default GroceryOrderButton;
