import React, { useState } from 'react';
import RealGroceryCartService from './RealGroceryCartService.js';
import CartLimitationsGuide from './CartLimitationsGuide.jsx';

const GroceryOrderButton = ({ ingredients, dishName }) => {
  const [loading, setLoading] = useState({});
  const [cartStatus, setCartStatus] = useState({});

  // Get grocery apps from service
  const groceryApps = RealGroceryCartService.getAvailableApps().reduce((acc, app) => {
    acc[app.key] = {
      name: app.name,
      icon: app.key === 'blinkit' ? 'üõí' : app.key === 'zepto' ? '‚ö°' : 'üõçÔ∏è',
      color: app.key === 'blinkit' ? '#ff6b35' : app.key === 'zepto' ? '#00b894' : '#0984e3',
      ...app
    };
    return acc;
  }, {});

  // Function to create search query from ingredients array
  const createSearchQuery = (ingredients) => {
    if (!ingredients || !Array.isArray(ingredients) || ingredients.length === 0) {
      return null;
    }

    // Extract ingredient names and clean them
    const ingredientNames = ingredients.map(ingredient => {
      // Handle both string and object formats
      const ingredientName = typeof ingredient === 'string' 
        ? ingredient 
        : ingredient.ingredient || ingredient.name || '';

      // Clean the ingredient name (remove quantities, units, etc.)
      return ingredientName
        .replace(/[\d.,\/\s]+(?:grams?|g|kilos?|kg|ounces?|oz|pounds?|lbs?|cups?|tablespoons?|tbsp|teaspoons?|tsp|ml|l|pieces?|pcs?)/gi, '')
        .replace(/[^\w\s]/g, '')
        .trim();
    })
    .filter(name => name.length > 0); // Remove empty strings

    return ingredientNames.join('+');
  };

  // Function to add ingredients to grocery app cart
  const addToGroceryCart = async (appKey) => {
    if (!ingredients || ingredients.length === 0) {
      alert('No ingredients available for ordering. Please try again.');
      return;
    }

    setLoading(prev => ({ ...prev, [appKey]: true }));

    try {
      const result = await RealGroceryCartService.addToCart(appKey, ingredients);

      setCartStatus(prev => ({ 
        ...prev, 
        [appKey]: { 
          success: result.success, 
          message: result.message,
          method: result.method 
        } 
      }));

      if (result.success) {
        // Show success message with more details
        const ingredientsList = RealGroceryCartService.getIngredientsList(ingredients);
        const ingredientsText = ingredientsList.map(item => item.display).join(', ');

        let message = `‚úÖ ${result.message}\n\nIngredients: ${ingredientsText}`;

        // Add search queries info if available
        if (result.searchQueries && result.searchQueries.length > 1) {
          message += `\n\nSearch queries opened:`;
          result.searchQueries.forEach((query, index) => {
            message += `\n${index + 1}. ${query}`;
          });
        }

        message += `\n\nNote: Multiple tabs may have opened. You'll need to manually add items to cart in the ${groceryApps[appKey].name} app.`;

        setTimeout(() => {
          alert(message);
        }, 500);
      }
    } catch (error) {
      console.error(`Error opening ${groceryApps[appKey].name}:`, error);
      setCartStatus(prev => ({ 
        ...prev, 
        [appKey]: { 
          success: false, 
          message: 'Failed to open app',
          method: 'error' 
        } 
      }));
    } finally {
      setLoading(prev => ({ ...prev, [appKey]: false }));
    }
  };

  // Check if ingredients are available
  const hasIngredients = ingredients && Array.isArray(ingredients) && ingredients.length > 0;
  const searchQuery = createSearchQuery(ingredients);

  if (!hasIngredients) {
    return (
      <div className="grocery-order-section">
        <div className="no-ingredients-message">
          <p>‚ö†Ô∏è No ingredients available for this dish.</p>
          <p>Please try selecting a different dish or contact support.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="grocery-order-section">
                        <div className="grocery-order-header">
                    <h3>üõí Order Ingredients</h3>
                    <p>Open grocery apps with ingredients pre-loaded for easy ordering</p>
                  </div>

      <div className="ingredients-preview">
        <h4>Ingredients to order:</h4>
        <div className="ingredients-list">
          {ingredients.map((ingredient, index) => {
            const ingredientName = typeof ingredient === 'string' 
              ? ingredient 
              : ingredient.ingredient || ingredient.name || 'Unknown ingredient';
            return (
              <span key={index} className="ingredient-tag">
                {ingredientName}
              </span>
            );
          })}
        </div>
      </div>

      <div className="grocery-apps-grid">
        {Object.entries(groceryApps).map(([appKey, app]) => {
          const isLoading = loading[appKey];
          const status = cartStatus[appKey];

          return (
            <button
              key={appKey}
              className={`grocery-app-button ${isLoading ? 'loading' : ''} ${status?.success ? 'success' : ''}`}
              onClick={() => addToGroceryCart(appKey)}
              disabled={isLoading}
              style={{ '--app-color': app.color }}
            >
              {isLoading ? (
                <>
                  <span className="app-icon">‚è≥</span>
                  <span className="app-name">Adding to {app.name}...</span>
                  <span className="app-subtitle">Please wait</span>
                </>
              ) : status?.success ? (
                <>
                  <span className="app-icon">‚úÖ</span>
                  <span className="app-name">Added to {app.name}</span>
                  <span className="app-subtitle">{status.method}</span>
                </>
              ) : (
                <>
                  <span className="app-icon">{app.icon}</span>
                  <span className="app-name">Open {app.name}</span>
                  <span className="app-subtitle">With ingredients search</span>
                </>
              )}
            </button>
          );
        })}
      </div>

                        <div className="search-preview">
                    <p className="search-preview-text">
                      <strong>Search query:</strong> {searchQuery}
                    </p>
                  </div>

                  {/* Cart Limitations Guide */}
                  <CartLimitationsGuide />
                </div>
              );
};

export default GroceryOrderButton;
