import React, { useState } from 'react';

const DishDetails = ({ dish, ingredients, recipeInfo, onClose, onOrderNow }) => {
  const [showOrderForm, setShowOrderForm] = useState(false);
  const [orderData, setOrderData] = useState({
    quantity: 1,
    address: '',
    paymentMethod: 'Cash on Delivery'
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setOrderData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleOrderSubmit = (e) => {
    e.preventDefault();

    if (!orderData.address.trim()) {
      alert('Please enter a delivery address');
      return;
    }

    const order = {
      dish: recipeInfo?.title || dish,
      quantity: parseInt(orderData.quantity),
      address: orderData.address.trim(),
      paymentMethod: orderData.paymentMethod,
      ingredients: ingredients,
      totalPrice: (12.99 * orderData.quantity).toFixed(2),
      orderDate: new Date().toISOString()
    };

    onOrderNow(order);
    setShowOrderForm(false);
    setOrderData({ quantity: 1, address: '', paymentMethod: 'Cash on Delivery' });
  };

  if (showOrderForm) {
    return (
      <div className="dish-details-modal">
        <div className="dish-details-content">
          <button className="close-button" onClick={() => setShowOrderForm(false)}>×</button>

          <div className="order-form">
            <h3>Order Confirmation</h3>
            <div className="order-dish-info">
              <h4>Dish: {recipeInfo?.title || dish}</h4>
              <p className="dish-price">Price: ${(12.99 * orderData.quantity).toFixed(2)}</p>
            </div>

            <form onSubmit={handleOrderSubmit}>
              <div className="form-group">
                <label htmlFor="order-qty">Quantity:</label>
                <input
                  type="number"
                  id="order-qty"
                  name="quantity"
                  min="1"
                  value={orderData.quantity}
                  onChange={handleInputChange}
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="order-address">Delivery Address:</label>
                <textarea
                  id="order-address"
                  name="address"
                  value={orderData.address}
                  onChange={handleInputChange}
                  placeholder="Enter your full delivery address"
                  required
                  rows="3"
                />
              </div>

              <div className="form-group">
                <label htmlFor="order-payment">Payment Method:</label>
                <select
                  id="order-payment"
                  name="paymentMethod"
                  value={orderData.paymentMethod}
                  onChange={handleInputChange}
                >
                  <option value="Cash on Delivery">Cash on Delivery</option>
                  <option value="Online Payment" disabled>Online Payment (Coming Soon)</option>
                </select>
              </div>

              <div className="order-summary">
                <h4>Order Summary</h4>
                <p>Dish: {recipeInfo?.title || dish}</p>
                <p>Quantity: {orderData.quantity}</p>
                <p>Total: ${(12.99 * orderData.quantity).toFixed(2)}</p>
              </div>

              <div className="order-actions">
                <button type="button" className="cancel-button" onClick={() => setShowOrderForm(false)}>
                  Cancel
                </button>
                <button type="submit" className="place-order-button">
                  Place Order
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="dish-details-modal">
      <div className="dish-details-content">
        <button className="close-button" onClick={onClose}>×</button>

        <div className="dish-details">
          <div className="dish-header">
            <h2 id="dish-name">{recipeInfo?.title || dish}</h2>
            <div className="dish-image-container">
              <img 
                id="dish-image" 
                src={recipeInfo?.image || 'https://images.unsplash.com/photo-1565299624946-b28f40a0ca4b?w=400&h=300&fit=crop&crop=center'} 
                alt={recipeInfo?.title || dish}
                className="dish-image"
              />
            </div>
            <p id="dish-price" className="dish-price">$12.99</p>
          </div>

          <div className="dish-ingredients">
            <h3>Ingredients</h3>
            <ul id="dish-ingredients">
              {ingredients.map((ingredient, index) => {
                const quantity = ingredient.quantity;
                const unit = ingredient.unit || '';
                const name = ingredient.ingredient || 'Unknown ingredient';

                return (
                  <li key={index} className="ingredient-item">
                    <span className="ingredient-amount">
                      {quantity !== null ? `${quantity} ${unit}` : ''}
                    </span>
                    <span className="ingredient-name">{name}</span>
                  </li>
                );
              })}
            </ul>
          </div>

          <div className="dish-actions">
            <button className="order-now-button" onClick={() => setShowOrderForm(true)}>
              Order Now
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DishDetails;
