// Real Grocery Cart Service - Actually opens grocery apps with ingredients

class RealGroceryCartService {
  constructor() {
    this.apps = {
      blinkit: {
        name: 'Blinkit',
        webUrl: 'https://blinkit.com',
        appDeepLink: 'blinkit://',
        searchUrl: 'https://blinkit.com/search?q=',
        cartUrl: 'https://blinkit.com/cart',
        hasRealAPI: false
      },
      zepto: {
        name: 'Zepto',
        webUrl: 'https://www.zepto.in',
        appDeepLink: 'zepto://',
        searchUrl: 'https://www.zepto.in/search?q=',
        cartUrl: 'https://www.zepto.in/cart',
        hasRealAPI: false
      },
      bigbasket: {
        name: 'BigBasket',
        webUrl: 'https://www.bigbasket.com',
        appDeepLink: 'bigbasket://',
        searchUrl: 'https://www.bigbasket.com/ps/?q=',
        cartUrl: 'https://www.bigbasket.com/cart',
        hasRealAPI: false
      }
    };
  }

  // Add ingredients to cart using real methods
  async addToCart(appKey, ingredients) {
    const app = this.apps[appKey];
    if (!app) {
      throw new Error(`Unknown grocery app: ${appKey}`);
    }

    try {
      // Method 1: Try to open mobile app with ingredients
      if (this.isMobileDevice()) {
        const mobileResult = await this.openMobileApp(app, ingredients);
        if (mobileResult.success) {
          return mobileResult;
        }
      }

      // Method 2: Open web app with ingredients in search
      const webResult = await this.openWebApp(app, ingredients);
      if (webResult.success) {
        return webResult;
      }

      // Method 3: Fallback to general app opening
      return this.openAppGeneral(app, ingredients);

    } catch (error) {
      console.error(`Error opening ${app.name}:`, error);
      return {
        success: false,
        error: `Failed to open ${app.name}: ${error.message}`
      };
    }
  }

  // Check if user is on mobile device
  isMobileDevice() {
    return /Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent);
  }

  // Open mobile app with ingredients
  async openMobileApp(app, ingredients) {
    try {
      // Create search query from ingredients
      const searchQuery = this.createSearchQuery(ingredients);

      // Try to open app with search query
      const deepLinkUrl = `${app.appDeepLink}search?q=${encodeURIComponent(searchQuery)}`;

      // Open the deep link
      window.location.href = deepLinkUrl;

      // Wait a bit to see if app opens
      await this.delay(2000);

      return {
        success: true,
        method: 'mobile_app',
        message: `Opened ${app.name} mobile app with ingredients`,
        url: deepLinkUrl
      };

    } catch (error) {
      console.log('Mobile app deep link failed:', error);
      return { success: false };
    }
  }

  // Open web app with ingredients
  async openWebApp(app, ingredients) {
    try {
      // Create multiple search queries for better results
      const searchQueries = this.createMultipleSearchQueries(ingredients);

      // Open main search with first query
      const mainSearchUrl = `${app.searchUrl}${encodeURIComponent(searchQueries[0])}`;
      window.open(mainSearchUrl, '_blank', 'noopener,noreferrer');

      // Open additional searches for other ingredients (max 3 tabs)
      const additionalQueries = searchQueries.slice(1, 3);
      additionalQueries.forEach((query, index) => {
        setTimeout(() => {
          const searchUrl = `${app.searchUrl}${encodeURIComponent(query)}`;
          window.open(searchUrl, '_blank', 'noopener,noreferrer');
        }, (index + 1) * 500); // Delay each tab opening
      });

      return {
        success: true,
        method: 'web_app_multiple',
        message: `Opened ${app.name} with ${searchQueries.length} ingredient searches`,
        url: mainSearchUrl,
        searchQueries: searchQueries
      };

    } catch (error) {
      console.error('Web app opening failed:', error);
      return { success: false };
    }
  }

  // Create multiple search queries for better results
  createMultipleSearchQueries(ingredients) {
    if (!ingredients || !Array.isArray(ingredients) || ingredients.length === 0) {
      return ['groceries'];
    }

    const queries = [];

    // Prioritize common ingredients that are more likely to be found
    const commonIngredients = this.getCommonIngredients(ingredients);

    // Create individual queries for common ingredients
    commonIngredients.slice(0, 3).forEach(ingredient => {
      const cleanName = this.cleanIngredientName(ingredient);
      if (cleanName.length > 0) {
        queries.push(cleanName);
      }
    });

    // If we have more ingredients, create a grouped query
    if (ingredients.length > 3) {
      const remainingIngredients = ingredients.slice(3, 6);
      const groupedQuery = remainingIngredients
        .map(ingredient => {
          const name = typeof ingredient === 'string' 
            ? ingredient 
            : ingredient.ingredient || ingredient.name || '';
          return this.cleanIngredientName(name);
        })
        .filter(name => name.length > 0)
        .slice(0, 2)
        .join(' ');

      if (groupedQuery.length > 0) {
        queries.push(groupedQuery);
      }
    }

    return queries.slice(0, 4); // Max 4 queries
  }

  // Get common ingredients that are more likely to be found in grocery stores
  getCommonIngredients(ingredients) {
    const commonKeywords = [
      'cheese', 'milk', 'butter', 'onion', 'tomato', 'garlic', 'ginger',
      'salt', 'pepper', 'oil', 'flour', 'bread', 'rice', 'pasta',
      'potato', 'carrot', 'cucumber', 'lettuce', 'spinach', 'mushroom',
      'chicken', 'beef', 'fish', 'egg', 'yogurt', 'cream'
    ];

    return ingredients
      .map(ingredient => {
        const name = typeof ingredient === 'string' 
          ? ingredient 
          : ingredient.ingredient || ingredient.name || '';
        return name;
      })
      .sort((a, b) => {
        const aCommon = commonKeywords.some(keyword => 
          this.cleanIngredientName(a).includes(keyword)
        );
        const bCommon = commonKeywords.some(keyword => 
          this.cleanIngredientName(b).includes(keyword)
        );

        // Sort common ingredients first
        if (aCommon && !bCommon) return -1;
        if (!aCommon && bCommon) return 1;
        return 0;
      });
  }

  // Group ingredients by type for better search
  groupIngredientsByType(ingredients) {
    const groups = {
      'dairy': [],
      'vegetables': [],
      'spices': [],
      'grains': [],
      'other': []
    };

    const dairyKeywords = ['cheese', 'milk', 'butter', 'cream', 'yogurt', 'paneer'];
    const vegetableKeywords = ['onion', 'tomato', 'garlic', 'ginger', 'pepper', 'spinach', 'bell'];
    const spiceKeywords = ['salt', 'pepper', 'cumin', 'turmeric', 'coriander', 'chili'];
    const grainKeywords = ['flour', 'bread', 'rice', 'pasta', 'flatbread'];

    ingredients.forEach(ingredient => {
      const name = typeof ingredient === 'string' 
        ? ingredient 
        : ingredient.ingredient || ingredient.name || '';
      const cleanName = this.cleanIngredientName(name);

      if (dairyKeywords.some(keyword => cleanName.includes(keyword))) {
        groups.dairy.push(name);
      } else if (vegetableKeywords.some(keyword => cleanName.includes(keyword))) {
        groups.vegetables.push(name);
      } else if (spiceKeywords.some(keyword => cleanName.includes(keyword))) {
        groups.spices.push(name);
      } else if (grainKeywords.some(keyword => cleanName.includes(keyword))) {
        groups.grains.push(name);
      } else {
        groups.other.push(name);
      }
    });

    return groups;
  }

  // Open app generally (fallback)
  openAppGeneral(app, ingredients) {
    try {
      // Just open the main app URL
      window.open(app.webUrl, '_blank', 'noopener,noreferrer');

      return {
        success: true,
        method: 'general',
        message: `Opened ${app.name} app. Please search for ingredients manually.`,
        url: app.webUrl,
        ingredients: ingredients
      };

    } catch (error) {
      console.error('General app opening failed:', error);
      return { success: false };
    }
  }

  // Create search query from ingredients
  createSearchQuery(ingredients) {
    if (!ingredients || !Array.isArray(ingredients) || ingredients.length === 0) {
      return '';
    }

    // Get the most important ingredients (first 2-3)
    const importantIngredients = ingredients.slice(0, 3);

    const ingredientNames = importantIngredients.map(ingredient => {
      const name = typeof ingredient === 'string' 
        ? ingredient 
        : ingredient.ingredient || ingredient.name || '';

      // Clean the ingredient name
      return this.cleanIngredientName(name);
    })
    .filter(name => name.length > 0);

    // Join with + for better search results
    return ingredientNames.join('+');
  }

  // Clean ingredient name for better search
  cleanIngredientName(name) {
    return name
      .replace(/[\d.,\/\s]+(?:grams?|g|kilos?|kg|ounces?|oz|pounds?|lbs?|cups?|tablespoons?|tbsp|teaspoons?|tsp|ml|l|pieces?|pcs?)/gi, '')
      .replace(/[^\w\s]/g, '')
      .trim()
      .toLowerCase();
  }

  // Utility function for delays
  delay(ms) {
    return new Promise(resolve => setTimeout(resolve, ms));
  }

  // Get cart status for an app
  async getCartStatus(appKey) {
    const app = this.apps[appKey];
    if (!app) {
      return { available: false, message: 'App not supported' };
    }

    return {
      available: true,
      hasApp: this.isMobileDevice(),
      hasWebApp: true,
      hasRealAPI: app.hasRealAPI,
      message: `${app.name} is available (${this.isMobileDevice() ? 'mobile app' : 'web app'})`
    };
  }

  // Get all available apps
  getAvailableApps() {
    return Object.keys(this.apps).map(key => ({
      key,
      ...this.apps[key]
    }));
  }

  // Get ingredients list for display
  getIngredientsList(ingredients) {
    if (!ingredients || !Array.isArray(ingredients)) {
      return [];
    }

    return ingredients.map(ingredient => {
      const name = typeof ingredient === 'string' 
        ? ingredient 
        : ingredient.ingredient || ingredient.name || '';

      const quantity = ingredient.quantity || 1;
      const unit = ingredient.unit || '';

      return {
        name: name,
        quantity: quantity,
        unit: unit,
        display: unit ? `${quantity} ${unit} ${name}` : `${quantity} ${name}`
      };
    });
  }
}

export default new RealGroceryCartService();
